type CheckpointInput struct {
	header                 string
	transactions           []string
	opReturnTransactionIdx int
	expectedOpReturnData   string
}

func InputsToSpvProofs(inputs []*CheckpointInput) []*btcctypes.BTCSpvProof {
	var spvs []*btcctypes.BTCSpvProof

	for _, input := range inputs {
		headerBytes, err := hex.DecodeString(input.header)

		if err != nil {
			panic("Inputs should containg valid hex encoded header")
		}

		var txBytes [][]byte

		for _, t := range input.transactions {
			tbytes, err := hex.DecodeString(t)

			if err != nil {
				panic("Inputs should contain valid hex encoded transactions")
			}

			txBytes = append(txBytes, tbytes)
		}

		spv, err := dg.SpvProofFromHeaderAndTransactions(headerBytes, txBytes, uint(input.opReturnTransactionIdx))

		if err != nil {
			panic("Inputs should contain valid spv hex encoded data")
		}

		spvs = append(spvs, spv)
	}

	return spvs
}

func extractOpReturnFomInputs(inputs []*CheckpointInput) []byte {
	var opReturnData []byte

	for _, in := range inputs {
		dec, e := hex.DecodeString(in.expectedOpReturnData)

		if e != nil {
			panic("Inputs should contain valid hexencoded data")
		}

		opReturnData = append(opReturnData, dec...)
	}

	return opReturnData
}

